import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Col, Row } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom';
import categories from '../categories';
import './Home.css';
import { useDispatch, useSelector } from 'react-redux';
import { updateProducts } from '../features/productSlice';
import ProductPreview from '../components/ProductPreview';

function Home() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);
  const [searchResults, setSearchResults] = useState([]);
  const lastProducts = products.slice(0, 8);

  useEffect(() => {
    axios
      .get('http://localhost:8080/products')
      .then(({ data }) => dispatch(updateProducts(data)));
  }, [dispatch]);

  function handleSearch(query) {
    axios
      .get(`http://localhost:8080/search/${query}`)
      .then(({ data }) => setSearchResults(data))
      .catch((error) => console.log(error));
  }

  return (
    <div>
      <div className="featured-products-container container mt-4">
        <h2>Last products</h2>
        {searchResults.length === 0 ? (
          <div className="d-flex justify-content-center flex-wrap">
            {lastProducts.map((product) => (
              <ProductPreview {...product} key={product.id} />
            ))}
          </div>
        ) : (
          <div className="d-flex justify-content-center flex-wrap">
            {searchResults.map((product) => (
              <ProductPreview {...product} key={product.id} />
            ))}
          </div>
        )}
        <div>
          <Link
            to="/category/all"
            style={{
              textAlign: 'right',
              display: 'block',
              textDecoration: 'none',
            }}
          >
            See more {'>>'}
          </Link>
        </div>
      </div>
      {/* sale banner */}
      <div className="sale__banner--container mt-4">
        <img
          src="https://res.cloudinary.com/learn-code-10/image/upload/v1654093280/xkia6f13xxlk5xvvb5ed.png"
          alt=""
        />
      </div>
      <div className="recent-products-container container mt-4">
        <h2>Categories</h2>
        <Row>
          {categories.map((category) => (
            <LinkContainer
              to={`/category/${category.name.toLocaleLowerCase()}`}
              key={category.id}
            >
              <Col md={4}>
                <div
                  style={{
                    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${category.img})`,
                    gap: '10px',
                  }}
                  className="category-tile"
                >
                  {category.name}
                </div>
              </Col>
            </LinkContainer>
          ))}
        </Row>
      </div>
    </div>
  );
}

export default Home;
