import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import React from 'react';
import { Alert, Col, Container, Row, Table } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import CheckoutForm from '../components/CheckoutForm';
import {
  useIncreaseCartProductMutation,
  useDecreaseCartProductMutation,
  useRemoveFromCartMutation,
} from '../appApi';

const stripePromise = loadStripe(
  'pk_test_51NJ7MPJnBsLyYEwfYhxYAZYN1aOZkyyixlHlEUBxELQ8JVH38o5cWqiJ9B4VPgBETtOaw2iTTL0pGBPaGYYFX2yB00HrqGWDUw'
);

const styles = `
  .fa-plus-circle,
  .fa-minus-circle {
    cursor: pointer;
    display: inline-block;
  }

  .fa-plus-circle {
    margin-left: 5px;
  }

  .fa-minus-circle {
    margin-right: 5px;
  }

  .table-responsive-sm {
    max-height: 70vh;
    overflow: scroll;
  }
`;

function CartPage() {
  const user = useSelector((state) => state.user);
  const products = useSelector((state) => state.products);
  const userCartObj = user.cart;
  let cart = products.filter((product) => userCartObj[product._id] != null);
  const [increaseCart] = useIncreaseCartProductMutation();
  const [decreaseCart] = useDecreaseCartProductMutation();
  const [removeFromCart, { isLoading }] = useRemoveFromCartMutation();

  function handleDecrease(product) {
    const quantity = user.cart[product._id];
    if (quantity <= 1) return alert("Can't decrease further");
    decreaseCart({
      productId: product._id,
      price: product.price,
      userId: user._id,
    });
  }

  function handleIncrease(product) {
    increaseCart({
      productId: product._id,
      price: product.price,
      userId: user._id,
    });
  }

  return (
    <Container style={{ minHeight: '95vh' }} className="cart-container">
      <style>{styles}</style>
      <Row>
        <Col>
          <h1 className="pt-2 h3">Shopping Bag</h1>
          {cart.length === 0 ? (
            <Alert variant="info">
              Shopping bag is empty. Add products to your bag.
            </Alert>
          ) : (
            <Elements stripe={stripePromise}>
              <CheckoutForm />
            </Elements>
          )}
        </Col>
        {cart.length > 0 && (
          <Col md={5}>
            <>
              <Table responsive="sm" className="cart-table">
                <thead>
                  <tr>
                    <th>&nbsp;</th>
                    <th>Product</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {/* loop through cart products */}
                  {cart.map((item) => (
                    <tr key={item._id}>
                      <td>&nbsp;</td>
                      <td>
                        {!isLoading && (
                          <i
                            className="fa fa-times"
                            style={{ marginRight: 10, cursor: 'pointer' }}
                            onClick={() =>
                              removeFromCart({
                                productId: item._id,
                                price: item.price,
                                userId: user._id,
                              })
                            }
                          ></i>
                        )}
                        <img
                          src={item.pictures[0].url}
                          alt=""
                          style={{
                            width: 100,
                            height: 100,
                            objectFit: 'cover',
                          }}
                        />
                      </td>
                      <td>${item.price}</td>
                      <td>
                      <span className="quantity-indicator">
        <i
          className="fa fa-minus-circle"
          onClick={() => handleDecrease(item)}
        ></i>
        <span>{user.cart[item._id]}</span>
        <i
          className="fa fa-plus-circle"
          onClick={() => handleIncrease(item)}
        ></i>
      </span>
                      </td>
                      <td>${item.price * user.cart[item._id]}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
              <div>
                <h3 className="h4 pt-4">Total: ${user.cart.total}</h3>
              </div>
            </>
          </Col>
        )}
      </Row>
    </Container>
  );
}

export default CartPage;
