import axios from 'axios';
import React, { useEffect } from 'react';
import { Col, Row } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom';
import categories from '../categories';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import { updateProducts } from '../features/productSlice';
import ProductPreview from '../components/ProductPreview';
import Slider from '../components/Slider';

const CategoryTile = styled.div`
  height: 250px;
  background-size: cover;
  background-position: center;
  padding-left: 20px !important;
  padding-right: 20px !important;
  display: flex;
  justify-content: center;
  align-items: center;
  color: white;
  font-size: 30px;
  cursor: pointer;
  transition: all 0.3s;
  margin-top: 30px;

  &:hover {
    transform: scale(1.05);
  }
`;

const Card = styled.div`
  border: none !important;
  width: 15rem !important;
`;

const CardImg = styled.img`
  &.card-img-top.product-preview-img {
    border-radius: 10px;
  }
`;

const SaleBannerContainer = styled.div`
  img {
    max-width: 1200px;
    display: block;
    margin: 0 auto;
  }
`;

const FeaturedProductsContainer = styled.div`
  margin-top: 4rem;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;

  a {
    text-align: right;
    display: block;
    text-decoration: none;
  }
`;

const RecentProductsContainer = styled.div`
  margin-top: 4rem;
`;

function Home() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);
  const lastProducts = products.slice(0, 8);

  useEffect(() => {
    axios
      .get('http://localhost:8080/products')
      .then(({ data }) => dispatch(updateProducts(data)));
  }, [dispatch]);

  return (
    <div>
      <Slider />
      <FeaturedProductsContainer>
        {/* top products here */}
        <div className="d-flex justify-content-center flex-wrap">
          {lastProducts.map((product) => (
            <ProductPreview key={product.id} {...product} />
          ))}
        </div>
        <div>
          <Link to="/category/all">See more {'>>'}</Link>
        </div>
      </FeaturedProductsContainer>
      <RecentProductsContainer>
        <Row>
          {categories.map((category) => (
            <LinkContainer
              to={`/category/${category.name.toLocaleLowerCase()}`}
              key={category.id}
            >
              <Col md={4}>
                <CategoryTile
                  style={{
                    backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url(${category.img})`,
                    gap: '10px',
                  }}
                >
                  {category.name}
                </CategoryTile>
              </Col>
            </LinkContainer>
          ))}
        </Row>
      </RecentProductsContainer>
    </div>
  );
}

export default Home;
