import React, { useEffect } from 'react';
import { useDispatch, useSelector, useLocation } from 'react-redux';
import { updateProducts } from '../features/productSlice';
import axios from 'axios';
import ProductPreview from '../components/ProductPreview';

function SearchResults() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);
  const location = useLocation();
  const searchQuery = new URLSearchParams(location.search).get('q');

  useEffect(() => {
    // Make the API call to fetch the search results
    axios
      .get(`http://localhost:8080/products/search/${searchQuery}`)
      .then(({ data }) => dispatch(updateProducts(data)))
      .catch((error) => console.log(error));
  }, [dispatch, searchQuery]);

  return (
    <div>
      <h2>Search Results</h2>
      <div className="d-flex justify-content-center flex-wrap">
        {products.map((product) => (
          <ProductPreview key={product.id} {...product} />
        ))}
      </div>
    </div>
  );
}

export default SearchResults;
