import { Container, Nav, Tab, Col, Row } from 'react-bootstrap';
import ClientsAdminPage from '../components/ClientsAdminPage';
import DashboardProducts from '../components/DashboardProducts';
import OrdersAdminPage from '../components/OrdersAdminPage';
import NewProduct from './NewProduct';
import styled from 'styled-components';

const CustomNav = styled(Nav)`
  && {
    background-color: transparent;
  }

  .nav-link {
    color: black;
    text-decoration: none;
    font-size: 14px;
    transition: color 0.3s;
    cursor: pointer;
  }

  .nav-link:hover,
  .nav-link:focus,
  .nav-link:active {
    color: black !important;
    background-color: transparent !important;
    box-shadow: none !important;
    outline: 2px solid gray !important;
    font-weight: bold;
  }

  .nav-link:hover {
    background-color: transparent !important;
  }

  .nav-link:focus,
  .nav-link:active {
    box-shadow: none !important;
    background-color: transparent !important;
  }

  .nav-link:focus {
    outline-offset: -2px !important;
  }
`;

function AdminPanel() {
  return (
    <Container>
      <Tab.Container defaultActiveKey="products">
        <Row className="mt-4 mb-4">
          <Col sm={12}>
            <CustomNav variant="pills" className="justify-content-center">
              <Nav.Item>
                <Nav.Link eventKey="products">Products</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="orders">Orders</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="clients">Clients</Nav.Link>
              </Nav.Item>
              <Nav.Item>
                <Nav.Link eventKey="create-products">Create Product</Nav.Link>
              </Nav.Item>
            </CustomNav>
          </Col>
        </Row>
        <Row>
          <Col sm={12}>
            <Tab.Content>
              <Tab.Pane eventKey="products">
                <DashboardProducts />
              </Tab.Pane>
              <Tab.Pane eventKey="orders">
                <OrdersAdminPage />
              </Tab.Pane>
              <Tab.Pane eventKey="clients">
                <ClientsAdminPage />
              </Tab.Pane>
              <Tab.Pane eventKey="create-products">
                <NewProduct />
              </Tab.Pane>
            </Tab.Content>
          </Col>
        </Row>
      </Tab.Container>
    </Container>
  );
}

export default AdminPanel;
