import axios from '../axios';
import React, { useRef, useState } from 'react';
import { Navbar, Button, Nav, NavDropdown, Container, Form, FormControl } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { Link, NavLink } from 'react-router-dom';
import { logout, resetNotifications } from '../features/userSlice';
import { updateProducts } from '../features/productSlice';
import './Navigation.css';

function Navigation() {
  const user = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const bellRef = useRef(null);
  const notificationRef = useRef(null);
  const [bellPos, setBellPos] = useState({});
  const [searchQuery, setSearchQuery] = useState('');

  function handleLogout() {
    dispatch(logout());
  }

  const unreadNotifications = user?.notifications?.reduce((acc, current) => {
    if (current.status === 'unread') return acc + 1;
    return acc;
  }, 0);

  function handleToggleNotifications() {
    const position = bellRef.current.getBoundingClientRect();
    setBellPos(position);
    notificationRef.current.style.display =
      notificationRef.current.style.display === 'block' ? 'none' : 'block';
    dispatch(resetNotifications());
    if (unreadNotifications > 0)
      axios.post(`/users/${user._id}/updateNotifications`);
  }

  function handleSearch(e) {
    e.preventDefault();
    axios
      .get(`http://localhost:8080/products/search/${searchQuery}`)
      .then(({ data }) => dispatch(updateProducts(data)))
      .catch((error) => console.log(error));
  }

  function handleInputChange(e) {
    setSearchQuery(e.target.value);
  }

  return (
    <Navbar bg="light" expand="lg">
      <Container>
        <Link to="/">
          <Navbar.Brand>Flipzone</Navbar.Brand>
        </Link>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {/* Search Bar */}
            <Form className="d-flex" onSubmit={handleSearch}>
              {' '}
              {/* Add onSubmit event handler */}
              <FormControl
                type="search"
                placeholder="Search"
                className="mr-2"
                value={searchQuery}
                onChange={handleInputChange} // Add onChange event handler
              />
              <Button variant="outline-success" type="submit">
                Search
              </Button>{' '}
              {/* Change to type="submit" */}
            </Form>

            {/* if no user */}
            {!user && (
              <NavLink to="/login" className="nav-link">
                Login
              </NavLink>
            )}
            {user && !user.isAdmin && (
              <NavLink to="/cart" className="nav-link">
                <i className="fas fa-shopping-cart"></i>
                {user.cart?.count > 0 && (
                  <span className="badge badge-warning" id="cartcount">
                    {user.cart.count}
                  </span>
                )}
              </NavLink>
            )}

            {/* if user */}
            {user && (
              <>
                <NavLink
                  to="#"
                  className="nav-link"
                  style={{ position: 'relative' }}
                  onClick={handleToggleNotifications}
                >
                  <i
                    className="fas fa-bell"
                    ref={bellRef}
                    data-count={unreadNotifications || null}
                  ></i>
                </NavLink>
                <NavDropdown title={`${user.email}`} id="basic-nav-dropdown">
                  {user.isAdmin && (
                    <>
                      <NavLink to="/admin" className="dropdown-item">
                        Dashboard
                      </NavLink>
                    </>
                  )}
                  {!user.isAdmin && (
                    <>
                      <NavLink to="/cart" className="dropdown-item">
                        Cart
                      </NavLink>
                      <NavLink to="/orders" className="dropdown-item">
                        My orders
                      </NavLink>
                    </>
                  )}

                  <NavDropdown.Divider />
                  <Button
                    variant="danger"
                    onClick={handleLogout}
                    className="logout-btn"
                  >
                    Logout
                  </Button>
                </NavDropdown>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
      {/* notifications */}
      <div
        className="notifications-container"
        ref={notificationRef}
        style={{
          position: 'absolute',
          top: bellPos.top + 30,
          left: bellPos.left,
          display: 'none',
        }}
      >
        {user?.notifications?.length > 0 ? (
          user.notifications.map((notification) => (
            <p className={`notification-${notification.status}`}>
              {notification.message}
              <br />
              <span>
                {notification.time.split('T')[0] +
                  ' ' +
                  notification.time.split('T')[1]}
              </span>
            </p>
          ))
        ) : (
          <p>No notifications yet</p>
        )}
      </div>
    </Navbar>
  );
}

export default Navigation;
