import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateProducts } from '../features/productSlice';
import axios from 'axios';
import ProductPreview from '../components/ProductPreview';

function SearchResults({ searchQuery }) {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true); // Set loading state to true when fetching the search results

    // Make the API call to fetch the search results
    axios
      .get(`http://localhost:8080/products/search/${searchQuery}`)
      .then(({ data }) => {
        dispatch(updateProducts(data));
        setLoading(false); // Set loading state to false after fetching the search results
      })
      .catch((error) => {
        console.log(error);
        setLoading(false); // Set loading state to false in case of an error
      });
  }, [dispatch, searchQuery]);

  if (loading) {
    return <p>Loading...</p>; // Render a loading state while fetching the search results
  }

  if (!products || products.length === 0) {
    return <p>No results found.</p>; // Handle the case when the products array is empty or undefined
  }

  return (
    <div>
      <h2>Search Results</h2>
      <div className="d-flex justify-content-center flex-wrap">
        {products.map((product) => (
          <ProductPreview key={product.id} {...product} />
        ))}
      </div>
    </div>
  );
}

export default SearchResults;
