import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useSignupMutation } from '../appApi';
import axios from 'axios';

const SignupContainer = styled(Container)`
  @media screen and (max-width: 756px) {
    .signup__image--container {
      display: none;
    }
  }
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  height: 100vh;
  margin-top: 50px;
`;

const Signup = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [signup, { error, isLoading, isError }] = useSignupMutation();

  async function handleSignup(e) {
    e.preventDefault();
    const response = await signup({ name, email, password });

    if (response.data) {
      try {
        // Send email to the user
        const sendGridAPIKey = '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'; // Replace with your SendGrid API key
        const senderEmail = 'YOUR_SENDGRID_VERIFIED_EMAIL'; // Replace with your SendGrid verified email address

        await axios.post(
          'https://api.sendgrid.com/v3/mail/send',
          {
            personalizations: [
              {
                to: [{ email }],
                subject: 'Welcome to My App',
              },
            ],
            content: [
              {
                type: 'text/plain',
                value: 'Hello world!',
              },
            ],
            from: {
              email: senderEmail,
            },
          },
          {
            headers: {
              Authorization: `Bearer ${sendGridAPIKey}`,
              'Content-Type': 'application/json',
            },
          }
        );
      } catch (error) {
        console.error('Failed to send email:', error);
      }
    }
  }

  return (
    <SignupContainer>
      <Row className="justify-content-center align-items-start" style={{ height: '100vh' }}>
        <Col md={6}>
          <FormContainer>
            <Form style={{ width: '100%' }} onSubmit={handleSignup}>
              <h1>Create Account</h1>
              <br />
              {isError && <Alert variant="danger">{error.data}</Alert>}
              <Form.Group as={Row} controlId="formName">
                <Form.Label column md={3}>
                  Name
                </Form.Label>
                <Col md={9}>
                  <Form.Control
                    type="text"
                    placeholder="Your name"
                    value={name}
                    required
                    onChange={(e) => setName(e.target.value)}
                  />
                </Col>
              </Form.Group>
              <br />
              <Form.Group as={Row} controlId="formEmail">
                <Form.Label column md={3}>
                  Email Address
                </Form.Label>
                <Col md={9}>
                  <Form.Control
                    type="email"
                    placeholder="Enter email"
                    value={email}
                    required
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Col>
              </Form.Group>
              <br />
              <Form.Group as={Row} controlId="formPassword" className="mb-3">
                <Form.Label column md={3}>
                  Password
                </Form.Label>
                <Col md={9}>
                  <Form.Control
                    type="password"
                    placeholder="Enter Password"
                    value={password}
                    required
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </Col>
              </Form.Group>
              <br />
              <Form.Group>
                <Button
                  type="submit"
                  disabled={isLoading}
                  style={{ backgroundColor: 'black', borderColor: 'black', color: 'white' }}
                >
                  Create account
                </Button>
              </Form.Group>
              <p className="pt-3 text-center">
                Have an account already? <Link to="/login" style={{ color: 'black' }}>Login</Link>{' '}
              </p>
            </Form>
          </FormContainer>
        </Col>
      </Row>
    </SignupContainer>
  );
};

export default Signup;
